-- -----------------------------------------------------
-- Preprocessing
-- -----------------------------------------------------

UPDATE train_schedule
SET arrives_passes = 'A'
where arrival_time = '00:00:00' or dept_time = '00:00:00';


UPDATE train_schedule
SET dept_time = arrival_time 
where arrives_passes = 'P';

UPDATE train
SET train.start_location = 
(SELECT 
        station_name 
    FROM
        train_schedule
            INNER JOIN
        station ON station.station_no = train_schedule.station_no
    WHERE
        train_no = train.train_no
	order by distance
    limit 1
    );

UPDATE train
SET train.end_location = 
(SELECT 
        station_name 
    FROM
        train_schedule
            INNER JOIN
        station ON station.station_no = train_schedule.station_no
    WHERE
        train_no = train.train_no
	order by distance desc
    limit 1
    );


